CREATE TABLE IF NOT EXISTS django_migrations (id integer NOT NULL SERIAL PRIMARY KEY, app varchar(255) NOT NULL, name varchar(255) NOT NULL, applied datetime NOT NULL);
CREATE TABLE IF NOT EXISTS auth_group_permissions (id integer NOT NULL SERIAL PRIMARY KEY, group_id integer NOT NULL REFERENCES auth_group (id) DEFERRABLE INITIALLY DEFERRED, permission_id integer NOT NULL REFERENCES auth_permission (id) DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS auth_user_groups (id integer NOT NULL SERIAL PRIMARY KEY, user_id integer NOT NULL REFERENCES auth_user (id) DEFERRABLE INITIALLY DEFERRED, group_id integer NOT NULL REFERENCES auth_group (id) DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS auth_user_user_permissions (id integer NOT NULL SERIAL PRIMARY KEY, user_id integer NOT NULL REFERENCES auth_user (id) DEFERRABLE INITIALLY DEFERRED, permission_id integer NOT NULL REFERENCES auth_permission (id) DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS django_admin_log (id integer NOT NULL SERIAL PRIMARY KEY, object_id text NULL, object_repr varchar(200) NOT NULL, action_flag smallint unsigned NOT NULL CHECK (action_flag >= 0), change_message text NOT NULL, content_type_id integer NULL REFERENCES django_content_type (id) DEFERRABLE INITIALLY DEFERRED, user_id integer NOT NULL REFERENCES auth_user (id) DEFERRABLE INITIALLY DEFERRED, action_time datetime NOT NULL);
CREATE TABLE IF NOT EXISTS django_content_type (id integer NOT NULL SERIAL PRIMARY KEY, app_label varchar(100) NOT NULL, model varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS auth_permission (id integer NOT NULL SERIAL PRIMARY KEY, content_type_id integer NOT NULL REFERENCES django_content_type (id) DEFERRABLE INITIALLY DEFERRED, codename varchar(100) NOT NULL, name varchar(255) NOT NULL);
CREATE TABLE IF NOT EXISTS auth_group (id integer NOT NULL SERIAL PRIMARY KEY, name varchar(150) NOT NULL UNIQUE);
CREATE TABLE IF NOT EXISTS auth_user (id integer NOT NULL SERIAL PRIMARY KEY, password varchar(128) NOT NULL, last_login datetime NULL, is_superuser bool NOT NULL, username varchar(150) NOT NULL UNIQUE, last_name varchar(150) NOT NULL, email varchar(254) NOT NULL, is_staff bool NOT NULL, is_active bool NOT NULL, date_joined datetime NOT NULL, first_name varchar(150) NOT NULL);
CREATE TABLE IF NOT EXISTS django_session (session_key varchar(40) NOT NULL PRIMARY KEY, session_data text NOT NULL, expire_date datetime NOT NULL);
CREATE TABLE IF NOT EXISTS guardiao_emprestimomaterial (id integer NOT NULL SERIAL PRIMARY KEY, quantidade integer unsigned NOT NULL CHECK (quantidade >= 0), emprestimo_id bigint NOT NULL REFERENCES guardiao_emprestimo (id) DEFERRABLE INITIALLY DEFERRED, material_id bigint NOT NULL REFERENCES guardiao_material (id) DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS guardiao_emprestimo (id integer NOT NULL SERIAL PRIMARY KEY, destino varchar(200) NOT NULL, data_emprestimo datetime NOT NULL, data_devolucao datetime NULL, cliente_id bigint NOT NULL REFERENCES guardiao_cliente (id) DEFERRABLE INITIALLY DEFERRED, operador_id bigint NOT NULL REFERENCES guardiao_operador (id) DEFERRABLE INITIALLY DEFERRED, isAtiva bool NOT NULL);
CREATE TABLE IF NOT EXISTS guardiao_categoria (id integer NOT NULL SERIAL PRIMARY KEY, nome varchar(100) NOT NULL UNIQUE, descricao text NOT NULL);
CREATE TABLE IF NOT EXISTS guardiao_emprestimohistorico (id integer NOT NULL SERIAL PRIMARY KEY, status varchar(50) NOT NULL, data datetime NOT NULL, emprestimo_id bigint NOT NULL REFERENCES guardiao_emprestimo (id) DEFERRABLE INITIALLY DEFERRED, operador_id bigint NULL REFERENCES guardiao_operador (id) DEFERRABLE INITIALLY DEFERRED);
CREATE TABLE IF NOT EXISTS guardiao_cliente (id integer NOT NULL SERIAL PRIMARY KEY, nome varchar(100) NOT NULL, identidade varchar(20) NOT NULL, imagem_identidade varchar(100) NULL, cpf varchar(14) NULL UNIQUE, foto varchar(100) NULL, isAtivo bool NOT NULL, organizacao_militar varchar(100) NULL);
CREATE TABLE IF NOT EXISTS guardiao_operador (id integer NOT NULL SERIAL PRIMARY KEY, nome varchar(100) NOT NULL, identidade varchar(20) NOT NULL, user_id integer NOT NULL UNIQUE REFERENCES auth_user (id) DEFERRABLE INITIALLY DEFERRED, funcao varchar(100) NULL, nivel_acesso integer NOT NULL);
CREATE TABLE IF NOT EXISTS guardiao_material (id integer NOT NULL SERIAL PRIMARY KEY, nome varchar(100) NOT NULL, registro varchar(50) NULL UNIQUE, categoria_id bigint NULL REFERENCES guardiao_categoria (id) DEFERRABLE INITIALLY DEFERRED, quantidade_disponivel integer unsigned NOT NULL CHECK (quantidade_disponivel >= 0), quantidade_emprestada integer unsigned NOT NULL CHECK (quantidade_emprestada >= 0), quantidade_total integer unsigned NOT NULL CHECK (quantidade_total >= 0));
CREATE TABLE IF NOT EXISTS guardiao_case (id integer NOT NULL SERIAL PRIMARY KEY, descricao varchar(255) NOT NULL, responsavel varchar(100) NOT NULL, lacre varchar(50) NOT NULL UNIQUE, data_criacao datetime NOT NULL, data_atualizacao datetime NOT NULL);
CREATE TABLE IF NOT EXISTS guardiao_assinante (id integer NOT NULL SERIAL PRIMARY KEY, nome varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS guardiao_funcaoassinante (id integer NOT NULL SERIAL PRIMARY KEY, nome varchar(100) NOT NULL);
CREATE TABLE IF NOT EXISTS guardiao_prontoarmamento (id integer NOT NULL SERIAL PRIMARY KEY, data date NOT NULL, numero integer unsigned NOT NULL UNIQUE CHECK (numero >= 0), lacre varchar(50) NOT NULL, assinante_1_id bigint NOT NULL REFERENCES guardiao_assinante (id) DEFERRABLE INITIALLY DEFERRED, assinante_2_id bigint NOT NULL REFERENCES guardiao_assinante (id) DEFERRABLE INITIALLY DEFERRED, assinante_3_id bigint NOT NULL REFERENCES guardiao_assinante (id) DEFERRABLE INITIALLY DEFERRED, funcao_1_id bigint NOT NULL REFERENCES guardiao_funcaoassinante (id) DEFERRABLE INITIALLY DEFERRED, funcao_2_id bigint NOT NULL REFERENCES guardiao_funcaoassinante (id) DEFERRABLE INITIALLY DEFERRED, funcao_3_id bigint NOT NULL REFERENCES guardiao_funcaoassinante (id) DEFERRABLE INITIALLY DEFERRED, tabela text NOT NULL);
